typedef enum {
    NETIF_STA_IDX = 0,
    NETIF_AP_IDX,
    NETIF_NUM
} TUYA_NETIF_TYPE;

typedef struct {
    ip4_addr_t ip;
    ip4_addr_t netmask;
    ip4_addr_t gw;
} ty_netif_ip_info_s;
